import React, { useState } from 'react';
import Dropdown from './Dropdown';


const FirstDropDown = () => {

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  function DropdownOpen(){
    setIsDropdownOpen(!isDropdownOpen);
  }

  return (  
    <ul>
        <li>   <a href="/about us" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">About us</a></li>
        <li>   <a href="/Brands" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">Brands</a></li>
        <li> <a href="/sales" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">Sales</a></li>
  
        <li onClick={DropdownOpen}  className="relative   md:block transition-color "> {/*section  */}
        <button  className="block transition-color text-xl text-blue-950 hover:text-yellow-500 hover:text-2xl">categories</button>
    
     {/* dropdown menu*/}

  
  {isDropdownOpen&&(
  <div  className="border shadow-md text-center font-bold bg-blue-300  absolute space-y-1  w-48 my-3 right-0 rounded-lg  p-4  top-32" >
     <Dropdown />
  
  </div >) }
  
  </li>

</ul>
   
    )



}



export default FirstDropDown
