import React, { useState } from 'react';

const FirstDropDown = () => {
  const [isFirstDropdown, setIsFirstDropdown] = useState(false);
  const [isSecondDropdown, setIsSecondDropdown] = useState(false);

  const toggleFirstDropdown = () => {
    setIsFirstDropdown((prev) => !prev);
  };

  const toggleSecondDropdown = () => {
    setIsSecondDropdown((prev) => !prev);
  };

  return (
    <ul className="relative">
      <li>
        <button
          onClick={toggleFirstDropdown}
          className="transition-color text-xl text-blue-950 hover:text-yellow-500 hover:text-2xl"
        >
          First Dropdown
        </button>

        {isFirstDropdown && (
          <ul
            className="absolute bg-blue-300 p-4 mt-2 rounded-lg shadow-md text-center w-48"
          >
            <li>
              <a href="/about-us" className="block p-2 hover:text-yellow-500">
                About Us
              </a>
            </li>
            <li>
              <a href="/brands" className="block p-2 hover:text-yellow-500">
                Brands
              </a>
            </li>
          </ul>
        )}
      </li>

      <li className="relative">
        <button
          onClick={toggleSecondDropdown}
          className="transition-color text-xl text-blue-950 hover:text-yellow-500 hover:text-2xl"
        >
          Second Dropdown
        </button>

        {isSecondDropdown && (
          <ul
            className="absolute bg-blue-300 p-4 mt-2 rounded-lg shadow-md text-center w-48"
          >
            <li>
              <a href="/categories/electronics" className="block p-2 hover:text-yellow-500">
                Electronics
              </a>
            </li>
            <li>
              <a href="/categories/clothing" className="block p-2 hover:text-yellow-500">
                Clothing
              </a>
            </li>
          </ul>
        )}
      </li>
    </ul>
  );
};

export default FirstDropDown;
