import React from 'react'


const Dropdown = () => {



  return (  
    <div className="border shadow-md text-center font-bold bg-blue-300 md:hidden absolute space-y-1  w-48 my-3 right-0 rounded-lg  p-4  top-32">
           <a href="/home" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">Home</a>
           <a href="/Gaming" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">Gaming</a>
           <a href="/kitchen" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">Kitchen</a>
     </div>
    )



}



export default Dropdown
