import React, { useState } from "react";
import Dropdown from '.';

const FirstDropDown = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <ul>
      <li>
        <a
          href="/about-us"
          className="block transition-color p-2 text-xl my-1 text-blue-950 hover:text-yellow-500 hover:text-2xl"
        >
          About Us
        </a>
      </li>
      <li>
        <a
          href="/brands"
          className="block transition-color p-2 text-xl my-1 text-blue-950 hover:text-yellow-500 hover:text-2xl"
        >
          Brands
        </a>
      </li>
      <li>
        <a
          href="/sales"
          className="block transition-color p-2 text-xl my-1 text-blue-950 hover:text-yellow-500 hover:text-2xl"
        >
          Sales
        </a>
      </li>
      <li className="relative">
        <button
          onClick={toggleDropdown}
          className="block transition-color p-2 text-xl text-blue-950 hover:text-yellow-500 hover:text-2xl"
        >
          Categories
        </button>
        {isDropdownOpen && (
          <div className="border shadow-md text-center font-bold bg-blue-300 absolute space-y-1 w-48 my-3 right-0 rounded-lg p-4">
            <Dropdown />
          </div>
        )}
      </li>
    </ul>
  );
};

export default FirstDropDown;
