import React, { useState } from 'react';
import Dropdown from './Dropdown';


const FirstDropDown = () => {

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  function DropdownOpen(){
    setIsDropdownOpen(!isDropdownOpen);
  }

  return (  
    <>
           <a href="/about us" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">About us</a>
           <a href="/Brands" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">Brands</a>
           <a href="/sales" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">sales</a>
           <a href="/categories" className="block transition-color p-2 text-xl my-1text-blue-950  hover:text-yellow-500 hover:text-2xl">categories</a>
     
   
    </>
    )



}



export default FirstDropDown
